CREATE TABLE experience (id INT AUTO_INCREMENT NOT NULL, position VARCHAR(255) NOT NULL, place VARCHAR(255) NOT NULL, from_date DATE NOT NULL, to_date DATE DEFAULT NULL, description LONGTEXT NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci ENGINE = InnoDB;
CREATE TABLE skills (id INT AUTO_INCREMENT NOT NULL, category_id INT DEFAULT NULL, skill VARCHAR(255) NOT NULL, points INT NOT NULL, INDEX IDX_D531167012469DE2 (category_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci ENGINE = InnoDB;
CREATE TABLE social_links (id INT AUTO_INCREMENT NOT NULL, title VARCHAR(255) NOT NULL, icon VARCHAR(255) NOT NULL, link VARCHAR(255) NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci ENGINE = InnoDB;
CREATE TABLE resume_downloads (id INT AUTO_INCREMENT NOT NULL, download_at DATETIME NOT NULL, from_ip VARCHAR(255) NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci ENGINE = InnoDB;
CREATE TABLE skill_categories (id INT AUTO_INCREMENT NOT NULL, category VARCHAR(255) NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci ENGINE = InnoDB;
CREATE TABLE certification (id INT AUTO_INCREMENT NOT NULL, title VARCHAR(255) NOT NULL, `when` DATE NOT NULL, description LONGTEXT DEFAULT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci ENGINE = InnoDB;
CREATE TABLE snippets (snippet_id VARCHAR(255) NOT NULL, text LONGTEXT NOT NULL, PRIMARY KEY(snippet_id)) DEFAULT CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci ENGINE = InnoDB;
ALTER TABLE skills ADD CONSTRAINT FK_D531167012469DE2 FOREIGN KEY (category_id) REFERENCES skill_categories (id);
